    //Check if date of birth is a valid date
    private boolean CheckDate()
    {
        boolean validDate = false;

        String date = dateOfBirthTF.getText();
        if (date.length() != 10)
        {
            dateOfBirthTF.setText(null);
            return validDate;
        }

        //Break date to sub strings to check validity
        char slash1 = date.charAt(2);
        char slash2 = date.charAt(5);
        String dayS = date.substring(0,2);
        String monthS = date.substring(3,5);
        String yearS = date.substring(6);

        //Convert strings to int to compare dates
        int daysInt = Integer.parseInt(dayS);
        int monthInt = Integer.parseInt(monthS);
        int yearInt = Integer.parseInt(yearS);

        //Check date(accounts for leap years)
        if(slash1 != '/' || slash2 != '/' || (monthInt == 2 && ((yearInt % 4 == 0 && daysInt > 29) || (yearInt % 4 != 0 && daysInt > 28))) || daysInt > 31 || monthInt > 12 || yearInt < 1940 || yearInt > 2024)
        {
            dateOfBirthTF.setText(null);
        }
        else
        {
            validDate = true;
        }

        return validDate;
    }


