    private void deleteBActionPerformed(java.awt.event.ActionEvent evt)
    {//GEN-FIRST:event_deleteBActionPerformed
        athlModel = (DefaultTableModel) infoT.getModel();
        athlModel.removeRow(infoT.getSelectedRow());
    }//GEN-LAST:event_deleteBActionPerformed

    private void updateBActionPerformed(java.awt.event.ActionEvent evt)
    {//GEN-FIRST:event_updateBActionPerformed
         int row = infoT.getSelectedRow();

         if (codeTF.getText() != null)
         {
             infoT.getModel().setValueAt(codeTF.getText(), row, 0);
         }

        if (firstNameTF.getText() != null)
        {
            infoT.getModel().setValueAt(firstNameTF.getText(), row, 1);
        }

        if (lastNameTF.getText() != null)
        {
            infoT.getModel().setValueAt(lastNameTF.getText(), row, 2);
        }

        if (dateOfBirthTF.getText() != null)
        {
            infoT.getModel().setValueAt(dateOfBirthTF.getText(), row, 4);
        }

        if (contInfoTF.getText() != null)
        {
            infoT.getModel().setValueAt(contInfoTF.getText(), row, 5);
        }

        infoT.getModel().setValueAt(gender, row, 3);


    }//GEN-LAST:event_updateBActionPerformed

    private void registBActionPerformed(java.awt.event.ActionEvent evt)
    {//GEN-FIRST:event_registBActionPerformed
        Athlete athl = new Athlete(firstNameTF.getText(), lastNameTF.getText(), gender, contInfoTF.getText(), dateOfBirthTF.getText(), Integer.parseInt(codeTF.getText()), false, 2);
        Athletes.add(athl);

        CheckDate();

        athlModel = (DefaultTableModel) infoT.getModel();
        athlModel.insertRow(0, new Object[]{codeTF.getText(), firstNameTF.getText(), lastNameTF.getText(), gender, dateOfBirthTF.getText(), contInfoTF.getText()});
    }//GEN-LAST:event_registBActionPerformed

