    private void dateOfBirthTFKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_dateOfBirthTFKeyTyped
        char testInt = evt.getKeyChar();
        if(!(Character.isDigit(testInt)) && (testInt != '/'))
        {
            evt.consume();
        }
    }//GEN-LAST:event_dateOfBirthTFKeyTyped

